import React, { useState } from 'react';
import { FaTrash } from 'react-icons/fa';

const App = () => {
  const [messages, setMessages] = useState([
    { from: 'bot', text: "Hello! I'm your Finance Coach. I can help you with financial planning, budgeting, investing, and other money matters. Ask me anything about personal finance!" },
  ]);
  const [input, setInput] = useState('');
  const [chats, setChats] = useState([
    { title: 'Chat 1', messages: [] },
    { title: 'Chat 2', messages: [] },
    { title: 'Chat 3', messages: [] },
  ]);
  const [activeChatIndex, setActiveChatIndex] = useState(0);

  const sendMessage = () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { from: 'user', text: input }];
    setMessages(newMessages);
    setChats(prev => prev.map((chat, idx) =>
      idx === activeChatIndex ? { ...chat, messages: newMessages } : chat
    ));
    setInput('');
  };

  const switchChat = (index) => {
    setActiveChatIndex(index);
    setMessages(chats[index].messages);
  };

  const deleteChat = (index) => {
    const updatedChats = chats.filter((_, i) => i !== index);
    setChats(updatedChats);
    const newIndex = index === 0 ? 0 : index - 1;
    setActiveChatIndex(newIndex);
    setMessages(updatedChats[newIndex]?.messages || []);
  };

  const addNewChat = () => {
    const newChat = { title: `Chat ${chats.length + 1}`, messages: [] };
    const updatedChats = [...chats, newChat];
    setChats(updatedChats);
    setActiveChatIndex(updatedChats.length - 1);
    setMessages([]);
  };

  const handleQuickQuestion = (question) => {
    setInput(question);
    sendMessage();
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#0b1a2b', color: 'white' }}>
      {/* Sidebar */}
      <div style={{ width: '15%', borderRight: '1px solid #334155', display: 'flex', flexDirection: 'column', backgroundColor: '#0f172a' }}>
        {/* Header */}
        <div style={{ height: '6.65%', borderBottom: '1px solid #334155' }} className="d-flex align-items-center justify-content-center">
          <h5 className="text-white">FinanceGPT</h5>
        </div>

        {/* New Chat Button */}
        <div className="px-3 py-2">
          <button 
            onClick={addNewChat} 
            className="w-100 text-center rounded py-4" 
            style={{ backgroundColor: 'green', border: 'none', color: 'white' }}
          >
            + New Chat
          </button>
        </div>

        {/* Chat buttons */}
        <div style={{ flexGrow: 1, padding: '10px', overflowY: 'auto' }}>
          {chats.map((chat, idx) => (
            <div key={idx} className="position-relative mb-2">
              <button
                className="w-100 text-start text-white rounded"
                style={{ 
                  padding: '12px',
                  borderRadius: '10px', 
                  border: '1px solid transparent', 
                  backgroundColor: idx === activeChatIndex ? '#d3d3d3' : 'transparent',
                  color: idx === activeChatIndex ? '#000' : '#fff'
                }}
                onClick={() => switchChat(idx)}
                onMouseEnter={(e) => e.currentTarget.style.background = '#d3d3d3'}
                onMouseLeave={(e) => e.currentTarget.style.background = idx === activeChatIndex ? '#d3d3d3' : 'transparent'}
              >
                {chat.title}
                <button
                  className="position-absolute end-0 top-0 mt-1 me-1"
                  style={{ border: 'none', background: 'transparent' }}
                  onClick={(e) => { e.stopPropagation(); deleteChat(idx); }}
                  onMouseEnter={(e) => e.currentTarget.style.background = 'red'}
                  onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
                >
                  <Trash className="text-white" />
                </button>
              </button>
            </div>
          ))}
        </div>

        {/* Footer */}
        <div style={{ height: '5%', borderTop: '1px solid #334155' }} className="d-flex align-items-center px-2">
          <small className="text-white">FinanceGPT v1.0</small>
        </div>
      </div>

      {/* Chat Section */}
      <div style={{ width: '85%' }} className="d-flex flex-column">
        <div style={{ width: '75%', margin: '0 auto', flexGrow: 1, paddingTop: '10px', overflowY: 'auto' }}>
          {messages.map((msg, idx) => (
            <div key={idx} className={`mb-2 text-${msg.from === 'bot' ? 'start' : 'end'}`}> 
              <div className={`d-inline-block p-2 rounded ${msg.from === 'bot' ? 'bg-secondary text-white' : 'bg-primary text-white'}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>

        {/* Quick Questions */}
        <div className="d-flex justify-content-center gap-2 py-2">
          {["How to budget?", "Best investments?", "Save for retirement?"]
            .map((q, idx) => (
              <button key={idx} className="btn btn-outline-light rounded-pill" onClick={() => handleQuickQuestion(q)}>
                {q}
              </button>
          ))}
        </div>

        {/* Input Box */}
        <div className="d-flex justify-content-center py-3">
          <div style={{ width: '75%' }} className="d-flex">
            <input
              className="form-control me-2 bg-dark text-white border-secondary"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask your finance question..."
            />
            <button className="btn btn-primary" onClick={sendMessage}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
