import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';

const App = () => {
  const defaultBotMessage = "Hello! I'm your Finance Coach. I can help you with financial planning, budgeting, investing, and other money matters. Ask me anything about personal finance!";

  const [messages, setMessages] = useState([
    { from: 'bot', text: defaultBotMessage },
  ]);

  const [input, setInput] = useState('');
  const [chats, setChats] = useState([
    { title: 'Chat 1', messages: [{ from: 'bot', text: defaultBotMessage }] },
    { title: 'Chat 2', messages: [{ from: 'bot', text: defaultBotMessage }] },
    { title: 'Chat 3', messages: [{ from: 'bot', text: defaultBotMessage }] },
  ]);
  const [activeChatIndex, setActiveChatIndex] = useState(0);

  const sendMessage = () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { from: 'user', text: input }];
    setMessages(newMessages);
    setChats(prev => prev.map((chat, idx) =>
      idx === activeChatIndex ? { ...chat, messages: newMessages } : chat
    ));
    setInput('');
  };

  const switchChat = (index) => {
    setActiveChatIndex(index);
    setMessages(chats[index].messages);
  };

  const deleteChat = (index) => {
    const updatedChats = chats.filter((_, i) => i !== index);
    setChats(updatedChats);
    const newIndex = index === 0 ? 0 : index - 1;
    setActiveChatIndex(newIndex);
    setMessages(updatedChats[newIndex]?.messages || []);
  };

  const addNewChat = () => {
    const newChat = { title: `Chat ${chats.length + 1}`, messages: [{ from: 'bot', text: defaultBotMessage }] };
    setChats([...chats, newChat]);
    setActiveChatIndex(chats.length); // Switch to the newly added chat
    setMessages(newChat.messages); // Set messages for the new chat
  };

  return (
    <div className="d-flex vh-100" style={{ backgroundColor: '#0b1a2b', color: 'white' }}>
      {/* Sidebar */}
      <div style={{ width: '15%', borderRight: '1px solid #334155', display: 'flex', flexDirection: 'column', backgroundColor: '#0f172a' }}>

        {/* Header */}
        <div style={{ height: '6%', borderBottom: '1px solid #334155' }} className="d-flex align-items-center justify-content-center">
          <h5 className="text-white">Finance Coach</h5>
        </div>

        {/* New Conversation Button */}
        <div className="px-3 py-2 text-center">
          <Button 
            variant="success" 
            className="w-100 text-white rounded" 
            onClick={addNewChat}
            style={{ 
              height: '60px', 
              fontSize: '1.1rem', 
              backgroundColor: '#90EE90', // light green
              border: 'none', 
              color: '#000' // dark text on light background
            }}
          >
            + New Conversation
          </Button>
        </div>

        {/* Chat buttons */}
        <div style={{ flexGrow: 1, padding: '10px', overflowY: 'auto' }}>
          {chats.map((chat, idx) => (
            <div key={idx} className="position-relative mb-2">
              <Button
                variant="dark"
                className="w-100 text-start text-white d-flex align-items-center rounded"
                style={{ 
                  height: '45px', 
                  borderRadius: '10px', 
                  border: '1px solid transparent', 
                  backgroundColor: 'transparent',
                  fontSize: '1rem',
                  position: 'relative'
                }}
                onClick={() => switchChat(idx)}
                onMouseEnter={(e) => e.currentTarget.style.border = '1px solid #475569'}
                onMouseLeave={(e) => e.currentTarget.style.border = '1px solid transparent'}
              >
                <span className="flex-grow-1">{chat.title}</span>
                <span
                  style={{
                    cursor: 'pointer',
                    padding: '2px 6px',
                    color: 'white',
                    background: 'transparent',
                    borderRadius: '5px',
                    display: 'flex',
                    alignItems: 'center',
                    height: '100%' // ensures full button height alignment
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteChat(idx);
                  }}
                  onMouseEnter={(e) => e.currentTarget.style.background = 'red'}
                  onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
                >
                  &#x1F5D1;
                </span>
              </Button>
            </div>
          ))}
        </div>      

        {/* Footer */}
        <div style={{ height: '5%', borderTop: '1px solid #334155' }} className="d-flex align-items-center px-2">
          <small className="text-white">FinanceGPT v1.0</small>
        </div>
      </div>

      {/* Chat Section */}
      <div style={{ width: '80%' }} className="d-flex flex-column">
        <div style={{ flexGrow: 1, padding: '2% 5%', marginTop: '10%' }}>
        {messages.map((msg, idx) => (
          <div key={idx} className={`d-flex mb-3 ${msg.sender === 'user' ? 'justify-content-end' : 'justify-content-start'}`}>
              {msg.sender === 'bot' && (
                <i className="bi bi-robot fs-3 me-2 text-success align-self-start"></i>
              )}

              <div style={{ maxWidth: '70%', wordWrap: 'break-word' }}>
                <p className="m-0 text-white">{msg.text}</p>
              </div>

              {msg.sender === 'user' && (
                <i className="bi bi-person-circle fs-3 ms-2 text-primary align-self-start"></i>
              )}
            </div>
          ))}
        </div>


        {/* Quick Questions */}
        <div className="d-flex justify-content-center gap-2 py-2">
          {["How to budget?", "Best investments?", "Save for retirement?"]
            .map((q, idx) => (
              <Button key={idx} variant="outline-light" className="rounded-pill">
                {q}
              </Button>
          ))}
        </div>

        {/* Input Box */}
        <div className="d-flex justify-content-center py-3">
          <div style={{ width: '75%' }} className="d-flex">
            <input
              className="form-control me-2 bg-dark text-white border-secondary"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask your finance question..."
            />
            <Button variant="primary" onClick={sendMessage}>Send</Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
