import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import { Trash } from 'react-bootstrap-icons';

const App = () => {
  const [messages, setMessages] = useState([
    { from: 'bot', text: "Hello! I'm your Finance Coach. I can help you with financial planning, budgeting, investing, and other money matters. Ask me anything about personal finance!" },
  ]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setInput('');
  };

  return (
    <div className="d-flex vh-100">
      {/* Sidebar */}
      <div style={{ width: '20%', borderRight: '1px solid #ccc', display: 'flex', flexDirection: 'column' }}>
        {/* Header */}
        <div style={{ height: '10%', borderBottom: '1px solid #ccc' }} className="d-flex align-items-center justify-content-center">
          <h5>FinanceGPT</h5>
        </div>

        {/* Chat buttons */}
        <div style={{ flexGrow: 1, padding: '10px', overflowY: 'auto' }}>
          {["Chat 1", "Chat 2", "Chat 3"].map((title, idx) => (
            <div key={idx} className="position-relative mb-2">
              <Button
                variant="light"
                className="w-100 text-start rounded"
                style={{ borderRadius: '10px', border: '1px solid transparent', backgroundColor: 'transparent' }}
                onMouseEnter={(e) => e.currentTarget.style.border = '1px solid #ccc'}
                onMouseLeave={(e) => e.currentTarget.style.border = '1px solid transparent'}
              >
                {title}
                <Button
                  variant="light"
                  className="position-absolute end-0 top-0 mt-1 me-1"
                  style={{ border: 'none', background: 'transparent' }}
                  onMouseEnter={(e) => e.currentTarget.style.background = 'red'}
                  onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
                >
                  <Trash />
                </Button>
              </Button>
            </div>
          ))}
        </div>

        {/* Footer */}
        <div style={{ height: '5%', borderTop: '1px solid #ccc' }} className="d-flex align-items-center px-2">
          <small>FinanceGPT v1.0</small>
        </div>
      </div>

      {/* Chat Section */}
      <div style={{ width: '80%' }} className="d-flex flex-column">
        <div style={{ width: '75%', margin: '0 auto', flexGrow: 1, paddingTop: '10px', overflowY: 'auto' }}>
          {messages.map((msg, idx) => (
            <div key={idx} className={`mb-2 text-${msg.from === 'bot' ? 'start' : 'end'}`}> 
              <div className={`d-inline-block p-2 rounded ${msg.from === 'bot' ? 'bg-light' : 'bg-primary text-white'}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>

        {/* Quick Questions */}
        <div className="d-flex justify-content-center gap-2 py-2">
          {["How to budget?", "Best investments?", "Save for retirement?"]
            .map((q, idx) => (
              <Button key={idx} variant="outline-secondary" className="rounded-pill">
                {q}
              </Button>
          ))}
        </div>

        {/* Input Box */}
        <div className="d-flex justify-content-center py-3">
          <div style={{ width: '75%' }} className="d-flex">
            <input
              className="form-control me-2"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask your finance question..."
            />
            <Button variant="primary" onClick={sendMessage}>Send</Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
